mapper.select:
SELECT u.id, u.dept_id, u.username, u.nick_name, u.sex, u.phone, u.email, u.avatar_name, u.avatar_path, u.`password`, u.is_admin, u.enabled, u.create_by, u.update_by, u.pwd_reset_time, u.create_time, u.update_time, GROUP_CONCAT( DISTINCT ur.role_id ) AS roleIds, GROUP_CONCAT( DISTINCT uj.job_id ) AS jobIds FROM sys_user u LEFT JOIN sys_user_role ur ON u.id = ur.user_id LEFT JOIN sys_user_job uj ON u.id = uj.user_id WHERE u.is_delete = 0
AND u.username LIKE CONCAT('%',#{user.username},'%')
AND u.email = #{user.email}
AND u.enabled = #{enabled}
GROUP BY u.id, u.dept_id, u.username, u.nick_name, u.sex, u.phone, u.email, u.avatar_name, u.avatar_path, u.`password`, u.is_admin, u.enabled, u.create_by, u.update_by, u.pwd_reset_time, u.create_time, u.update_time ORDER BY u.update_time DESC
